                    function getMonthDay() {
                        var date = new Date();
                        date.setMonth(date.getMonth() + 1);
                        date.setDate(0);
                        var days = date.getDate();
                        return days;
                    }

                    timeCode = _month + (_date / getMonthDay())
                    var springC = 20.646;
